import React, { useState } from 'react'

const StateData = () => {
  const [data,dataChange]=useState(0);
  const incriment= ()=>{
    dataChange(data+1);
  }
  return (
    <div>
      <div className="row">
        <div className="col-12">
            <h1>counter : <span className={'badge bg-${data==0 ? "danger" : data<0 ? "warning" : "primary"}'}>{data}</span></h1>
            <div className="buttone mt-5">
              <button className='px-5 py-1 bg-light border border-danger border-3 fs-4 mx-3'
              onClick={incriment}
              >Incriment</button>
              <button className='px-5 py-1 bg-light border border-danger border-3 fs-4 mx-3'>Rrset</button>
              <button className='px-5 py-1 bg-light border border-danger border-3 fs-4 mx-3'>Decriment</button>
            </div>
            <hr className='border border-danger border-2 '/>
        </div>
      </div>

    </div>
  )
}

export default StateData
